import React, { useState, useEffect } from 'react'
import { useTheme } from '@mui/material/styles';
import { Grid, Container, Typography, Button } from '@mui/material';
import Dashboard from '../../app/Appdashboard'
import Page from '../pures/Page';
import { getDateFromApi } from '../../Services/axiosService';
import DashboardRefresh from './DashboardRefresh';

export default function ShowData({ refresh }) {

    const [date, setDate] = useState([]);
    const [meditions, setMeditions] = useState([]);
    
    useEffect(() => {
        effect
        return () => {
            cleanup
        };
    }, [input]);


    const obtainDate = () => {
        getDateFromApi()
            .then((response) => {
                if (response.status === 200) {//Si la respuesta esta ok, guardo la fecha en la variable date. 
                    const tempDate = [...date];
                    const tempMeditions = [...meditions];
                    let value = (Math.random()*10).toFixed(0);


                    tempDate.push(response.data.toString());
                    tempMeditions.push(value.toString());
                    setMeditions(tempMeditions);
                    setDate(tempDate);
                    console.log(date);
                }
            })
            .catch((error) => alert(`An error has ocurred ${error}`))
    }

    return (

        <Page title='Dashboard'>
            <Container maxWidth='x1'>
                <Typography variant="h4" sx={{ mb: 5 }}>
                    Mediciones
                </Typography>
                <Grid item xs={12} md={6} lg={8}>
                <Dashboard
            title="Tension bateria"
            subheader="Panel solar 500mA"
            chartLabels={date}
            chartData={[
                // {
                //     name: 'Team A',
                //     type: 'column',
                //     fill: 'solid',
                //     data: [23, 11, 22, 27, 13, 22, 37, 21, 44, 22, 30],
                // },
                {
                    name: 'Team B',
                    type: 'area',
                    fill: 'gradient',
                    data: meditions
                },

            ]}
        />
                </Grid>
                <Grid item xs={12} md={6} lg={4}>
                    <Button onClick={obtainDate} variant="contained">Press</Button>
                </Grid>
            </Container>
        </Page>

    )
}
